  --color-brand: #52616B; /* barva potemnitve */
  --color-accent: #36b37e;
  --color-accent-2: #0d2436;
  --color-light: #fcfcfd;
  --color-shade: #cbcace;
  --color-inverted: white; /* obrnjena barva*/
  --color-tint: #e5eaf4;

  /* Base values */
  --color: var(--color-base);
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 13px;
  --border-color: #eee;
  --background: white;

  /* sizes */
    --size-xxs: 0.5rem;
    --size-xs: 0.75rem;
    --size-sm: 0.875rem;
    --size-base: 1rem;
    --size-lg: 1.125rem;
    --size-xl: 1.25rem;
    --size-2xl: 1.5rem;
    --size-3xl: 1.875rem;
    --size-4xl: 2.25rem;
    --size-5xl: 3rem;
    --size-6xl: 3.75rem;
    --size-7xl: 4.5rem;
    --size-8xl: 6rem;
    --size-9xl: 8rem;
    --size-10xl: 10rem;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--font-body);
  color: var(--color-base);
  line-height: 1.2;
  font-size: 1.125rem;
  background: var(--background);
}

.section.has-content {
  display: flex;
  justify-content: center;
  padding: 5rem 2rem;

  .content {
    max-width: 800px;
    width: 100%;
  }
}

.section-container {
  max-width: 1536px;
  margin-left: auto;
  margin-right: auto;
  padding-left: 0.5rem;
  padding-right: 0.5rem;
  padding-top: 3rem;
  padding-bottom: 3rem;

}

/* 2xl */
@media (max-width: 1536px) {
  .section-container {
    max-width: 1280px;
    font-size: var(--size-2xl);

  }
}

/* xl */
@media (max-width: 1280px) {
  .section-container {
    max-width: 1024px;
    font-size: var(--size-xl);
  }
}

/* lg */
@media (max-width: 1024px) {
  .section-container {
    max-width: 768px;
    font-size: var(--size-lg);
  }
}

/* md */
@media (max-width: 768px) {
  .section-container {
    max-width: 640px;
    font-size: var(--size-md);
  }
}

/* sm */
@media (max-width: 640px) {
  .section-container {
    max-width: 475px;
    font-size: var(--size-sm);
  }
}

/* xs */
@media (max-width: 475px) {
  .section-container {
    width: 100%;
    font-size: var(--size-xxs);
  }
}

.heading-group {
  display: grid;
  gap: 1rem;
  place-content: center;
  text-align: center;

  .superhead {
    font-family: var(--font-body);
    color: var(--color-accent);
    font-size: 0.875rem;
    font-weight: 500;
    letter-spacing: 1.5px;
    text-transform: uppercase;
  }

  .subheading {
    color: #4f6373;
    line-height: 1.4;
    max-width: 600px;
    font-weight: 400;
    max-width: 600px;
    margin: 0 auto;
  }
}

.heading {
  font-family: var(--font-heading);
  font-size: 2rem;
  line-height: 1.1;
  font-weight: 500;
  max-width: 600px;
}

.button {
  color: var(--color-brand, white);
  background: var(--color-inverted);
  border: 2px solid var(--color-brand);
  border-radius: 6px;
  padding: 8px 20px;
  transition: 0.1s background, 0.1s color;

  &:hover {
    color: var(--color-inverted);
    background: var(--color-brand);
    border-color: var(--color-inverted);
  }

  &.inverted {
    background: var(--color-white);
    color: var(--color-brand);
    border-color: #0d2436;
  }
}

.link {
  font-size: 1.125rem;
  font-weight: 400;
  color: var(--color-brand);

  .arrow {
    transition: transform 0.1s;
  }

